@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

[] a sh:ValidationReport ;
    sh:conforms true .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

[] a sh:ValidationReport ;
    sh:conforms false ;
    sh:result [ a sh:ValidationResult ;
            sh:focusNode <http://example.org/ApplicationSchema#MyGeometryCollection> ;
            sh:resultMessage "An instance of geo:GeometryCollection should only have outgoing rdfs:member relations to geo:Geometry instances." ;
            sh:resultPath rdfs:member ;
            sh:resultSeverity sh:Violation ;
            sh:sourceConstraint [ sh:select """\r
			PREFIX geo: <http://www.opengis.net/ont/geosparql#>\r
			PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>\r
			SELECT $this\r
			WHERE {\r
				$this rdfs:member ?geometry .\r
				FILTER NOT EXISTS {\r
					?feature geo:hasGeometry ?geometry .\r
				}\r
			}\r
		""" ] ;
            sh:sourceConstraintComponent sh:SPARQLConstraintComponent ;
            sh:sourceShape <file:///github/workspace/_sources/vocabulary/classes/geometrycollection/S23-GeometryCollectionClass-member-onlyGeometry-sub> ] .
